import MainNews from "../components/New/MainNews";
import SubNews from "../components/New/SubNews";
import { API_URL } from "../config/Constants";
import { useState, useEffect } from "react";

export default function ListNews({}) {

    const [articlesList, setArticlesList] = useState([])
    useEffect(() => {
        try {
            const getArticlesList = async () => {
                const response = await fetch(API_URL + '/api/articles/getArticlesByCategory/:' + 'thoi-su', {
                    method: 'GET',
                    headers: {
                        Accept: 'application/json',
                        'Content-Type': 'application/json',
                        Authorization: undefined,
                    },
                });
                const json = await response.json();
                setArticlesList(json.data);
            }
            getArticlesList()
        } catch (e) {
            console.log(e)
        }
    }, []);

    const length = articlesList.length();

    return (
        <div className=" ml-[140px] mr-[140px] mt-5">
            <div className="grid grid-cols-2 gap-5 ">
                <div>
                < div className="sm:gap-5 mb-3">
                        {
                            articlesList.slice(0, 1).map((article) => (
                                article &&
                                    <MainNews data={article} />
                            ))
                        }
            
                    </div>
                    <hr className="border-gray-300 bg-gray-300" />
                    <div className="mt-5">
                        {
                            articlesList.slice(2, length/2 - 3).map((article) => (
                                article &&
                                    <SubNews data={article} />
                            ))
                        }
                    </div>
                </div>
                <div>
                    {
                        articlesList.slice(length/2 - 2, ).map((article) => (
                            article &&
                                <SubNews data={article} />
                        ))
                    }
                </div>
            </div>
            
        </div>
    )
}
