import MainNews from "../components/New/MainNews";
import SubNews from "../components/New/SubNews";
import TopTitle from "./New/TopTitle";
import { API_URL } from "../config/Constants";
import { useState, useEffect } from "react";

// import LoadingV1 from "../../components/LoadingV1";
// import LoadingV2 from "../../components/LoadingV2";
// import LazyLoad from 'react-lazyload'
export default function ListNews({ articles, reload }) {

    const [ThoiSuList, setThoiSuList] = useState([])
    useEffect(() => {
        try {
            const getThoiSuList = async () => {
                const response = await fetch(API_URL + '/api/articles/getArticlesByCategory/thoi-su', {
                    method: 'GET',
                    headers: {
                        Accept: 'application/json',
                        'Content-Type': 'application/json',
                        Authorization: undefined,
                    },
                });
                const json = await response.json();
                setThoiSuList(json.data);
            }
            getThoiSuList()
        } catch (e) {
            console.log(e)
        }
    }, []);

    return (
        <div className=" ml-[140px] mr-[140px] mt-5">
            <div className="grid grid-cols-2 gap-5 ">
                <div>
                    <TopTitle title={'Tin nóng'} />
                    <div className="sm:gap-5 mb-3">
                        <MainNews />
                    </div>
                    <hr className="border-gray-300 bg-gray-300" />
                    <div className="mt-5">
                        <SubNews />
                        <SubNews />
                        <SubNews />
                    </div>
                </div>
                <div>
                        <SubNews />
                        <SubNews />
                        <SubNews />
                        <SubNews />
                        <SubNews />
                        <SubNews />
                    </div>
            </div>
            
        </div>
    )
}
